)VALUES(
    SEQ_PRODUCT_NO.NEXTVAL
  , #{productBrand}
  , #{productTitle}
  , #{productPrice}
  , #{productBenefit}
  , #{productNotice}
  , '식품'
  , 0
  , 0
  , #{categoryId}
  , #{pMembership}
)

mapper.update:
 UPDATE PRODUCT
	SET PRODUCT_STOCK = #{productStock}
