mapper.insert:
INSERT 
INTO PRODUCT
(
	PRODUCT_NO
  , PRODUCT_BRAND
  , PRODUCT_TITLE
  , PRODUCT_PRICE
  , PRODUCT_BENEFIT
  , PRODUCT_NOTICE
  , PRODUCT_INFO
  , PRODUCT_VIEW
  , PRODUCT_STOCK
  , CATEGORY_ID
  , P_MEMBERSHIP
)VALUES(
    SEQ_PRODUCT_NO.NEXTVAL
  , #{productBrand}
  , #{productTitle}
  , #{productPrice}
  , #{productBenefit}
  , #{productNotice}
  , '식품'
  , 0
  , 0
  , #{categoryId}
  , #{pMembership}
)

mapper.update:
 UPDATE PRODUCT
	SET PRODUCT_STOCK = #{productStock}
  WHERE PRODUCT_NO = #{productNo}

mapper.insert:
INSERT INTO COUPON(
      P_NO
    , P_NAME
    , P_SALE
    , START_DATE
    , END_DATE
)VALUES(
    SEQ_P_NO.NEXTVAL
    , #{pName}
    , #{pSale}
    , #{startDate}
    , #{endDate}
)

mapper.update:
UPDATE NOTICE 
SET N_TITLE = #{nTitle}
  , N_CONTENT = #{nContent}
WHERE N_NO = #{nNo}

UPDATE NOTICE 
SET N_VIEW = N_VIEW + 1
WHERE N_NO = #{nNo}
