	INSERT INTO ATT_CHECK(
        ATTENDANCE_ID
      , ATTENDANCE_DATE
      , USER_NO
	)VALUES (
        SEQ_ATTENDANCE_ID.NEXTVAL
      , TO_CHAR(#{attendanceDate}, 'YY-MM-DD')
      , #{userNo}
	)

mapper.select:
SELECT TO_CHAR(ATTENDANCE_DATE, 'FMDD') ATTENDANCE_DATE
  FROM ATT_CHECK
 WHERE USER_NO = #{userNo}

