mapper.update:
UPDATE TRAINER
   SET T_AREA_INTRO = #{tAreaIntro}
     , T_ONE_INTRO = #{tOneIntro}
     , T_PRICE = #{tPrice}
     , T_AREA = #{tArea}
     , T_METRO = #{tMetro}
     , WEEKDAY_SC = #{weekDaySc}
     , WEEKAND_SC = #{weekAndSc}
     , T_INTRO = #{tIntro}
 where T_NO = #{tNo}

